                        'CONFIGURAÇÕES:CONFIGURAÇÕES',
                        'CONFIGURAÇÕES:LOGOFF'
                    ]
                }
            }
        });
    }, 10);
}

/*=============================== Habilitar Botões após o Login ==========================================*/

if (msg.payload === '8504') {

    // 1. Habilita o BOTÃO que leva à página 'PARÂMETROS'
    node.send({
        payload: {
            // Use o 'topic' EXATO do seu botão (definido no nó UI Button)
            topic: 'TESTE', 
            enabled: true
        }
    });

    // 2. Habilita o BOTÃO que leva à página 'IHM SETUP'
    node.send({
        payload: {
            topic: 'botao_ihm_setup', 
            enabled: true
        }
    });

    // Repita para todos os botões restritos...

    // 3. Navega para a página de destino (CONFIGURAÇÕES)
    node.send({ payload: { 'page': 'CONFIGURAÇÕES' }});
}

/*============================ Função Logoff ================================*/
if (msg.payload === 'ON') {
    // 1. Saída 1: Controla o BOTÃO 'TESTE'
    // Apenas a propriedade 'enabled' é necessária na raiz da mensagem.
    var out1 = { 
        enabled: false // Estado que o botão 'TESTE' deve assumir
    }; 

    // 2. Saída 2: Controla o BOTÃO 'MANUTENÇÃO'
    var out2 = { 
        enabled: false // Estado que o botão 'MANUTENÇÃO' deve assumir
    }; 

    // Retorna as mensagens por três saídas: [Saída 1, Saída 2, Saída 3]
    return [out1, out2]; 
}

/*|||||||||||||||||||||||||||| Desabilita Botões quando há mudança de página||||||||||||||||||||||||||||*/ 

if (msg.payload === 'change') {
    // Verifica se msg.name corresponde a qualquer uma das páginas
    if (msg.name === 'HOME' || msg.name === 'ALARMES' || msg.name === 'RECEITAS' || msg.name === 'LOGIN') {
        // 1. Saída 1: Controla os botões restritos'
        var out1 = { 
            enabled: false 
        }; 

        // Retorna a mensagem para desabilitar os botões restritos
        return [out1]; 
    } 

    // Se msg.payload é 'change' mas msg.name não corresponde a nenhuma das páginas.
    return msg;
}

// Se msg.payload não é 'change', retorna a mensagem original.
return msg;

